import React, {useState} from 'react';
function RegistrationForm() {
    const [from, setForm] = useState({ name: '', email: '', age: ''});
    const [errors, setErrors] = useState({ name: '', email: '', age: ''});
    const [success, setSuccess] = useState(false);

    const validate = (name, value) => {
        switch (name) {
            case 'name':
                if (!value) return 'Требуется имя';
                if (value.length < 2) return 'Минимум 2 символа';
                return '';
            case 'email':
                if (!value) return 'Требуется email';
                if (!/^\S+@\S+\.\S+$/.test(value)) return 'Некорректный email';
                return '';

            case 'age':
                if (!value) return 'Требуется возраст';
                if (isNaN(value) || value < 18) return 'Возраст от 18 лет';
                return '';
            default:
                return '';
        }
    };

    const handleChange = (e) => {
        const {name, value} = e.target;
        setForm({ ...from, [name]: value});
        setErrors({ ...errors, [name]: validate(name, value) });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const nameErr = validate('name', form.name);
        const emailErr = validate('email', from.email);
        const ageErr = validate('age', form.age);

        if (nameErr || emailErr || ageErr) {
            setErrors({name: nameErr, email: emailErr, age: ageErr});
            return;
        }

        setSuccess(true);
        setForm({name:'', email:'', age:''});
        setTimeout(() => setSuccess(false), 3000);
    };
    return (
        <div style={{padding: 20, maxWidth: 400, margin:'auto'}} >
            <h2>Регистрация</h2>
            {success && <p style={{ color: 'green'}}>Успешно!</p>}

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: 15}}>
                    <input
                    type="text"
                    name="name"
                    placeholder="Имя"
                    value={form.name}
                    onChange={handleChange}
                    style={{width: '100%', padding: 8}}
                    />
                    {errors.name && <p style={{color:'red', margin:5}}>{errors.name}</p>}
                </div>
                <div style={{ marginBottom: 15}}>
                    <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={form.email}
                    onChange={handleChange}
                    style={{width:'100%',padding:8 }}
                    />
                    {errors.email && <p style={{ color:'red', margin:5}}>{errors.email}</p>}
                </div>
                <div style={{ marginBottom: 15}}>
                    <input
                    type="number"
                    name="age"
                    placeholder="Возраст"
                    value={form.age}
                    onChange={handleChange}
                    style={{width: '100%', padding: 8}}
                    />
                    {errors.age && <p style={{ color:'red',margin:5}}>{errors.age}</p>}
                </div>
                <button type="submit" style={{padiing: 10, width:'100%'}}>
                    Отправить
                </button>
            </form>
        </div>
    )

}

export default RegistrationForm;
