import React, { useState } from 'react';

function ArticleItem({ article, onClickRemove }) {
    const [isOpened, setIsOpened] = useState(false);
    
    return (
        <li style={{ border: '1px solid #ccc', marginBottom:10, padding:10}}>
            <div style={{ display: 'flex', justifyContent: 'space-between'}}>
                <span
                onClick={() => setIsOpened(!isOpened)}
                style={{ cursor: 'pointer', fontWeight: 'bold'}}
                >
                    {article.title} {isOpened ? '': ''}
                </span>
                <button onClick={() => onClickRemove(article.id)} style={{marginLeft: 10}}>
                    x
                </button>
            </div>
            {isOpened && <p style={{marginTop: 10}}>{article.summary}</p>}
        </li>
    );
}

export default ArticleItem;
